create table message (id varchar(255) not null, channel_id varchar(255), content TEXT, created_at datetime(6), media_file TEXT, parent_message_id varchar(255), reactions TEXT, recipient_id varchar(255), reminder TEXT, sender_id varchar(255) not null, type enum ('AUDIO','DOCUMENT','IMAGE','REMINDER','TEXT','UNSEND','VIDEO','VOTING'), voting TEXT, primary key (id)) engine=InnoDB;
create table request_message (id varchar(255) not null, content TEXT, created_at datetime(6), recipient_id varchar(255), sender_id varchar(255) not null, team_id varchar(255), primary key (id)) engine=InnoDB;
